#include &lt;fstream&gt;

using namespace std;

bool is_prime(int n) {
    if (n &lt; 2) {
        return false;
    }

    for(int i = 2; i * i &lt;= n; i++) {
        if (n % i == 0) {
            return false;
        }
    }

    return true;
}

int main() {
ifstream file("liczby.txt");
int data[100];
for(int i = 0; i &lt; 100; i++) {
    file &gt;&gt; data[i];
}

file.close();

int min_number = 0, min_value = 1000000, max_number = 0, max_value = 0;

bool primes[1000001] = {};
for(int i = 2; i &lt; 1000001; i++) {
    primes[i] = is_prime(i);
}

for(auto number : data) {
    int counter = 0;
    for(int i = 2; i &lt;= number / 2; i++) {
        if(primes[i] &amp;&amp; primes[number - i]) {
            counter++;
        }
    }

    if(counter &gt; max_value) {
        max_value = counter;
        max_number = number;
    } else if(counter &lt; min_value) {
        min_value = counter;
        min_number = number;
    }
}

cout &lt;&lt; "Zadanie 3.3" &lt;&lt; endl;
cout &lt;&lt; max_number &lt;&lt; " " &lt;&lt; max_value &lt;&lt; endl;
cout &lt;&lt; min_number &lt;&lt; " " &lt;&lt; min_value &lt;&lt; endl;
return 0;
}
</code></pre> <pre><code>def is_prime(n):
    if n &lt; 2:
        return False

    i = 2
    while i * i &lt;= n:
        if n % i == 0:
            return False

        i += 1

    return True

with open("liczby.txt") as file:
    data = [int(line) for line in file]

min_number = 0
min_value = 1000000
max_number = 0
