primes = [is_prime(i) for i in range(1000001)]

for number in data:
    counter = 0
    for i in range(2, number // 2 + 1):
        if primes[i] and primes[number - i]:
            counter += 1

    if counter &lt; min_value:
        min_value = counter
        min_number = number
    elif counter &gt; max_value:
        max_value = counter
        max_number = number

print("Zadanie 3.3")
print(max_number, max_value)
print(min_number, min_value)
