</code></pre> <pre><code>#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;algorithm&gt;

using namespace std;

bool is_niven(int n) {
    int sum = 0;
    while (n &gt; 0) {
        sum += n % 10;
        n /= 10;
    }
    return n % sum == 0;
}

int main() {
    ifstream file("niven.txt");
    int num;

    for (int i = 0; i &lt; 100; i++) {
        file &gt;&gt; num;
        if (is_niven(num)) {
            cout &lt;&lt; num &lt;&lt; endl;
        }
    }

    file.close();

    return 0;
}
